<?phprequire_once 'dbh.classes.php';class BorrowingHistory extends Dbh {    public function getUserBorrowHistory($useremail) {        $stmt = $this->connect()->prepare("SELECT books.title, books.author, borrow_records.borrow_date, borrow_records.return_date                                            FROM borrow_records                                            JOIN barcodes ON borrow_records.barcode = barcodes.barcode                                            JOIN books ON barcodes.isbn = books.isbn                                            WHERE borrow_records.borrower_email = ?");        $stmt->execute([$useremail]);        return $stmt->fetchAll(PDO::FETCH_ASSOC);    }    public function getBorrowingHistory($limit = 20, $offset = 0) {        $stmt = $this->connect()->prepare("SELECT books.title, users.user_name, borrow_records.borrower_email, borrow_records.borrow_date, borrow_records.return_date, barcodes.barcode FROM borrow_records          JOIN barcodes ON borrow_records.barcode = barcodes.barcode         JOIN books ON barcodes.isbn = books.isbn         JOIN users ON borrow_records.borrower_email = users.user_email                                                                        ORDER BY borrow_date DESC         LIMIT $limit OFFSET $offset");        $stmt->execute();        return $stmt->fetchAll(PDO::FETCH_ASSOC);    }}