<?phpsession_start();require_once '../classes/catalogue.classes.php';// Ensure only admins can access this functionalityif (!isset($_SESSION['isAdmin']) || !$_SESSION['isAdmin']) {    die("Unauthorized access.");}// Validate inputif (!isset($_POST['isbn'], $_POST['title'], $_POST['author'], $_POST['genre'], $_POST['year'], $_POST['summary'], $_POST['classmark'], $_POST['available_copies'])) {    die("Missing required fields.");}$isbn = $_POST['isbn'];$title = $_POST['title'];$author = $_POST['author'];$genre = $_POST['genre'];$year = (int)$_POST['year'];$summary = $_POST['summary'];$classmark = $_POST['classmark'];$available_copies = (int)$_POST['available_copies'];// Instantiate catalogue and update book$catalogue = new Catalogue();$catalogue->updateBook($isbn, $title, $author, $genre, $year, $summary, $classmark, $available_copies);// Redirect back to the catalogueheader("Location: ../catalogue.php");exit();